            <button
              onClick={() => {
                onNavigate('signup');
                setIsOpen(false);
              }}
              className="w-full mt-4 bg-blue-600 text-white px-4 py-4 rounded-xl text-base font-bold shadow-lg shadow-blue-200"
            >
              Request Account
            </button>
          </div>
        </div>
      )}
    </nav>
  );
};

