                  <option value="No">No</option>
                </select>
              </div>
              <div className="md:col-span-2">
                <label className="block text-sm font-semibold text-slate-700 mb-2">Compliance or Regulatory Requirements</label>
                <input 
                  type="text"
                  value={formData.complianceReqs}
                  onChange={e => updateField('complianceReqs', e.target.value)}
                  className="w-full px-4 py-3 rounded-xl border border-slate-200 focus:ring-2 focus:ring-blue-500 transition-all"
                  placeholder="e.g. GDPR, HIPAA, FINRA..."
                />
              </div>
              <div>
                <label className="block text-sm font-semibold text-slate-700 mb-2">Storage Requirements (per account)</label>
                <input 
                  type="text" required
                  value={formData.storageReqs}
                  onChange={e => updateField('storageReqs', e.target.value)}
                  className="w-full px-4 py-3 rounded-xl border border-slate-200 focus:ring-2 focus:ring-blue-500 transition-all"
                  placeholder="e.g. 20GB"
                />
              </div>
              <div>
                <label className="block text-sm font-semibold text-slate-700 mb-2">Verification Documents Ready?</label>
                <select 
                  value={formData.verificationDocs}
                  onChange={e => updateField('verificationDocs', e.target.value)}
                  className="w-full px-4 py-3 rounded-xl border border-slate-200 focus:ring-2 focus:ring-blue-500 transition-all"
                >
                  <option value="Yes">Yes, business license/ID ready</option>
                  <option value="No">No, I need more info</option>
                </select>
              </div>
            </div>
          </motion.div>
        );
      case 5:
        return (
          <motion.div initial={{ opacity: 0, x: 20 }} animate={{ opacity: 1, x: 0 }} className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div className="md:col-span-2">
                <label className="block text-sm font-semibold text-slate-700 mb-2">Security Expectations</label>
                <input 
                  type="text" required
                  value={formData.securityExpectations}
                  onChange={e => updateField('securityExpectations', e.target.value)}
                  className="w-full px-4 py-3 rounded-xl border border-slate-200 focus:ring-2 focus:ring-blue-500 transition-all"
                  placeholder="e.g. 2FA, IP Whitelisting, Custom Encryption"
                />
              </div>
              <div>
                <label className="block text-sm font-semibold text-slate-700 mb-2">Projected Start Timeline</label>
                <input 
                  type="text" required
                  value={formData.timeline}
                  onChange={e => updateField('timeline', e.target.value)}
                  className="w-full px-4 py-3 rounded-xl border border-slate-200 focus:ring-2 focus:ring-blue-500 transition-all"
                  placeholder="e.g. Next week, ASAP"
                />
              </div>
              <div className="md:col-span-2">
                <label className="block text-sm font-semibold text-slate-700 mb-2">Additional Notes / Special Requests</label>
                <textarea 
                  rows={2}
                  value={formData.additionalNotes}
                  onChange={e => updateField('additionalNotes', e.target.value)}
                  className="w-full px-4 py-3 rounded-xl border border-slate-200 focus:ring-2 focus:ring-blue-500 transition-all"
                  placeholder="Any other details for our team..."
                />
              </div>
              <div className="md:col-span-2 flex items-start space-x-3 mt-4">
                <input 
                  type="checkbox" required
                  checked={formData.agreedToTerms}
                  onChange={e => updateField('agreedToTerms', e.target.checked)}
                  className="mt-1 h-5 w-5 rounded border-slate-300 text-blue-600"
                />
                <span className="text-sm text-slate-600">
                  I agree to the <button onClick={() => onNavigate('terms')} className="text-blue-600 font-semibold underline">Terms of Service</button> and <button onClick={() => onNavigate('privacy')} className="text-blue-600 font-semibold underline">Privacy Policy</button>, and I confirm all submitted data is accurate for verification purposes.
                </span>
              </div>
            </div>
          </motion.div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="bg-slate-50 py-20 min-h-screen">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center mb-12">
          <h1 className="text-3xl font-bold text-slate-900">Request Your Professional Account</h1>
          <p className="text-slate-600 mt-2">Complete the questionnaire to begin the verification process.</p>
        </div>

        {/* Progress Bar */}
        <div className="mb-10 flex items-center justify-between relative px-2">
          <div className="absolute top-1/2 left-0 w-full h-0.5 bg-slate-200 -translate-y-1/2 -z-0"></div>
          <div 
            className="absolute top-1/2 left-0 h-0.5 bg-blue-600 -translate-y-1/2 transition-all duration-300 -z-0"
            style={{ width: `${((step - 1) / 4) * 100}%` }}
          ></div>
          {[1, 2, 3, 4, 5].map(i => (
            <div 
              key={i} 
              className={`w-10 h-10 rounded-full flex items-center justify-center font-bold text-sm z-10 transition-all duration-300 ${
                step >= i ? 'bg-blue-600 text-white' : 'bg-slate-200 text-slate-500'
              }`}
            >
              {step > i ? <CheckCircle2 className="w-6 h-6" /> : i}
            </div>
          ))}
        </div>

        <form onSubmit={handleSubmit} className="bg-white rounded-[2.5rem] shadow-xl border border-slate-100 overflow-hidden">
          <div className="p-8 md:p-12">
            <div className="flex items-center space-x-3 mb-8">
              <div className="p-3 bg-blue-50 rounded-xl">
                {step === 1 && <User className="w-6 h-6 text-blue-600" />}
                {step === 2 && <Mail className="w-6 h-6 text-blue-600" />}
                {step === 3 && <Building className="w-6 h-6 text-blue-600" />}
                {step === 4 && <Shield className="w-6 h-6 text-blue-600" />}
                {step === 5 && <HelpCircle className="w-6 h-6 text-blue-600" />}
              </div>
              <div>
                <h3 className="text-xl font-bold text-slate-900">
                  {step === 1 && 'Primary Information'}
                  {step === 2 && 'Email Usage Intent'}
                  {step === 3 && 'Business Details'}
                  {step === 4 && 'Infrastructure & Security'}
                  {step === 5 && 'Final Requirements'}
                </h3>
                <p className="text-sm text-slate-500">Step {step} of 5</p>
              </div>
            </div>

            {renderStep()}

            <div className="mt-12 pt-8 border-t border-slate-100 flex items-center justify-between">
              <button
                type="button"
                onClick={prevStep}
                disabled={step === 1}
                className={`flex items-center font-bold px-6 py-3 rounded-xl transition-all ${
                  step === 1 ? 'opacity-0 cursor-default' : 'text-slate-600 hover:bg-slate-50'
                }`}
              >
                <ChevronLeft className="w-5 h-5 mr-1" />
                Back
              </button>
              <button
                type="submit"
                className="bg-blue-600 text-white px-10 py-4 rounded-xl font-bold hover:bg-blue-500 transition-all flex items-center shadow-lg shadow-blue-200"
              >
                {step === 5 ? 'Submit Application' : 'Continue'}
                {step < 5 && <ChevronRight className="w-5 h-5 ml-2" />}
              </button>
            </div>
          </div>
        </form>

        <p className="mt-8 text-center text-xs text-slate-400">
          Your data is processed securely in compliance with GDPR. Account issuance is subject to manual verification.
        </p>
      </div>
    </div>
  );
};

