                  className="w-full px-4 py-3 rounded-xl border border-slate-200 focus:ring-2 focus:ring-blue-500 transition-all"
                  placeholder="e.g. Daily business communication, customer support, investor relations..."
                />
              </div>
              <div>
                <label className="block text-sm font-semibold text-slate-700 mb-2">Number of Email Addresses Needed</label>
                <input 
                  type="number" required
                  value={formData.emailCount}
                  onChange={e => updateField('emailCount', e.target.value)}
                  className="w-full px-4 py-3 rounded-xl border border-slate-200 focus:ring-2 focus:ring-blue-500 transition-all"
                  placeholder="e.g. 5"
                />
              </div>
              <div>
                <label className="block text-sm font-semibold text-slate-700 mb-2">Preferred Primary Username</label>
