          <div className="absolute top-1/2 left-0 w-full h-0.5 bg-slate-200 -translate-y-1/2 -z-0"></div>
          <div 
            className="absolute top-1/2 left-0 h-0.5 bg-blue-600 -translate-y-1/2 transition-all duration-300 -z-0"
            style={{ width: `${((step - 1) / 4) * 100}%` }}
          ></div>
          {[1, 2, 3, 4, 5].map(i => (
            <div 
              key={i} 
              className={`w-10 h-10 rounded-full flex items-center justify-center font-bold text-sm z-10 transition-all duration-300 ${
                step >= i ? 'bg-blue-600 text-white' : 'bg-slate-200 text-slate-500'
              }`}
            >
              {step > i ? <CheckCircle2 className="w-6 h-6" /> : i}
            </div>
          ))}
        </div>

        <form onSubmit={handleSubmit} className="bg-white rounded-[2.5rem] shadow-xl border border-slate-100 overflow-hidden">
          <div className="p-8 md:p-12">
            <div className="flex items-center space-x-3 mb-8">
              <div className="p-3 bg-blue-50 rounded-xl">
                {step === 1 && <User className="w-6 h-6 text-blue-600" />}
                {step === 2 && <Mail className="w-6 h-6 text-blue-600" />}
                {step === 3 && <Building className="w-6 h-6 text-blue-600" />}
                {step === 4 && <Shield className="w-6 h-6 text-blue-600" />}
                {step === 5 && <HelpCircle className="w-6 h-6 text-blue-600" />}
              </div>
              <div>
                <h3 className="text-xl font-bold text-slate-900">
                  {step === 1 && 'Primary Information'}
                  {step === 2 && 'Email Usage Intent'}
                  {step === 3 && 'Business Details'}
                  {step === 4 && 'Infrastructure & Security'}
                  {step === 5 && 'Final Requirements'}
                </h3>
                <p className="text-sm text-slate-500">Step {step} of 5</p>
              </div>
            </div>

            {renderStep()}

            <div className="mt-12 pt-8 border-t border-slate-100 flex items-center justify-between">
              <button
                type="button"
                onClick={prevStep}
                disabled={step === 1}
                className={`flex items-center font-bold px-6 py-3 rounded-xl transition-all ${
                  step === 1 ? 'opacity-0 cursor-default' : 'text-slate-600 hover:bg-slate-50'
                }`}
              >
                <ChevronLeft className="w-5 h-5 mr-1" />
                Back
              </button>
              <button
                type="submit"
                className="bg-blue-600 text-white px-10 py-4 rounded-xl font-bold hover:bg-blue-500 transition-all flex items-center shadow-lg shadow-blue-200"
              >
                {step === 5 ? 'Submit Application' : 'Continue'}
                {step < 5 && <ChevronRight className="w-5 h-5 ml-2" />}
              </button>
            </div>
          </div>
        </form>

        <p className="mt-8 text-center text-xs text-slate-400">
          Your data is processed securely in compliance with GDPR. Account issuance is subject to manual verification.
        </p>
      </div>
    </div>
  );
};

