              <div>
                <label className="block text-xs font-black uppercase tracking-widest text-slate-400 mb-3">Storage Quota per Account</label>
                <input 
                  type="text" required
                  value={formData.storageReqs}
                  onChange={e => updateField('storageReqs', e.target.value)}
                  className="w-full px-6 py-4 rounded-2xl border border-slate-200 focus:ring-2 focus:ring-blue-600 transition-all bg-slate-50 italic"
                  placeholder="e.g. 50GB"
                />
              </div>
              <div>
                <label className="block text-xs font-black uppercase tracking-widest text-slate-400 mb-3">Licence Documentation Ready?</label>
                <select 
                  value={formData.verificationDocs}
                  onChange={e => updateField('verificationDocs', e.target.value)}
                  className="w-full px-6 py-4 rounded-2xl border border-slate-200 focus:ring-2 focus:ring-blue-600 transition-all bg-slate-50 italic appearance-none"
                >
                  <option value="Yes">Yes, business licence ready</option>
                  <option value="No">No, I need guidance</option>
                </select>
              </div>
            </div>
          </motion.div>
        );
      case 5:
        return (
          <motion.div initial={{ opacity: 0, x: 20 }} animate={{ opacity: 1, x: 0 }} className="space-y-8">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
              <div className="md:col-span-2">
                <label className="block text-xs font-black uppercase tracking-widest text-slate-400 mb-3">Security Expectations</label>
                <input 
                  type="text" required
                  value={formData.securityExpectations}
                  onChange={e => updateField('securityExpectations', e.target.value)}
                  className="w-full px-6 py-4 rounded-2xl border border-slate-200 focus:ring-2 focus:ring-blue-600 transition-all bg-slate-50 italic"
                  placeholder="e.g. 2FA, IP Whitelisting, Custom Encryption"
                />
              </div>
              <div>
                <label className="block text-xs font-black uppercase tracking-widest text-slate-400 mb-3">Projected Start Programme</label>
                <input 
                  type="text" required
                  value={formData.timeline}
                  onChange={e => updateField('timeline', e.target.value)}
                  className="w-full px-6 py-4 rounded-2xl border border-slate-200 focus:ring-2 focus:ring-blue-600 transition-all bg-slate-50 italic"
                  placeholder="e.g. Q3 2024, ASAP"
                />
              </div>
              <div className="md:col-span-2">
                <label className="block text-xs font-black uppercase tracking-widest text-slate-400 mb-3">Additional Enquiries / Notes</label>
                <textarea 
                  rows={3}
                  value={formData.additionalNotes}
                  onChange={e => updateField('additionalNotes', e.target.value)}
                  className="w-full px-6 py-4 rounded-2xl border border-slate-200 focus:ring-2 focus:ring-blue-600 transition-all bg-slate-50 italic"
                  placeholder="Any other details for our London team..."
                />
              </div>
              <div className="md:col-span-2 flex items-start space-x-4 mt-6">
                <input 
                  type="checkbox" required
                  checked={formData.agreedToTerms}
                  onChange={e => updateField('agreedToTerms', e.target.checked)}
                  className="mt-1 h-6 w-6 rounded-lg border-slate-300 text-blue-600 focus:ring-blue-600 transition-all"
                />
                <span className="text-sm text-slate-500 font-medium italic leading-relaxed">
                  I agree to the <button onClick={() => onNavigate('terms')} className="text-blue-600 font-bold underline">Terms & Conditions</button> and <button onClick={() => onNavigate('privacy')} className="text-blue-600 font-bold underline">Privacy Policy</button>. I confirm all submitted data is accurate for vetting purposes.
                </span>
              </div>
            </div>
          </motion.div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="bg-slate-50 py-24 min-h-screen">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center mb-16">
          <h1 className="text-4xl font-black text-slate-900 tracking-tighter uppercase italic leading-none mb-4">Account <br />Verification.</h1>
          <p className="text-slate-500 text-lg font-medium italic">Complete the questionnaire to begin the mandatory vetting process.</p>
        </div>

        {/* Progress Bar */}
        <div className="mb-14 flex items-center justify-between relative px-4">
          <div className="absolute top-1/2 left-0 w-full h-1 bg-slate-200 -translate-y-1/2 rounded-full"></div>
          <div 
            className="absolute top-1/2 left-0 h-1 bg-blue-600 -translate-y-1/2 transition-all duration-700 rounded-full shadow-lg shadow-blue-500/30"
            style={{ width: `${((step - 1) / 4) * 100}%` }}
          ></div>
          {[1, 2, 3, 4, 5].map(i => (
            <div 
              key={i} 
              className={`w-12 h-12 rounded-2xl flex items-center justify-center font-black text-sm z-10 transition-all duration-500 ${
                step >= i ? 'bg-slate-900 text-white shadow-xl scale-110' : 'bg-white text-slate-300 border border-slate-100'
              }`}
            >
              {step > i ? <CheckCircle2 className="w-6 h-6" /> : i}
            </div>
          ))}
        </div>

        <form onSubmit={handleSubmit} className="bg-white rounded-[4rem] shadow-3xl border border-slate-100 overflow-hidden">
          <div className="p-12 md:p-16">
            <div className="flex items-center space-x-5 mb-12">
              <div className="p-4 bg-slate-50 rounded-2xl border border-slate-100 shadow-sm text-blue-600">
                {step === 1 && <User className="w-8 h-8" />}
                {step === 2 && <Mail className="w-8 h-8" />}
                {step === 3 && <Building className="w-8 h-8" />}
                {step === 4 && <Shield className="w-8 h-8" />}
                {step === 5 && <HelpCircle className="w-8 h-8" />}
              </div>
              <div>
                <h3 className="text-2xl font-black text-slate-900 tracking-tighter uppercase italic leading-none">
                  {step === 1 && 'Primary Information'}
                  {step === 2 && 'Email Usage Intent'}
                  {step === 3 && 'Organisation Details'}
                  {step === 4 && 'Infrastructure & Security'}
                  {step === 5 && 'Final Requirements'}
                </h3>
                <p className="text-xs font-black uppercase tracking-widest text-slate-400 mt-2">Stage {step} of 5</p>
              </div>
            </div>

            {renderStep()}

            <div className="mt-16 pt-10 border-t border-slate-50 flex items-center justify-between">
              <button
                type="button"
                onClick={prevStep}
                disabled={step === 1}
                className={`flex items-center font-black uppercase tracking-widest text-xs px-8 py-4 rounded-2xl transition-all ${
                  step === 1 ? 'opacity-0 cursor-default' : 'text-slate-400 hover:text-slate-900'
                }`}
              >
                <ChevronLeft className="w-5 h-5 mr-2" />
                Back
              </button>
              <button
                type="submit"
                className="bg-slate-900 text-white px-12 py-5 rounded-2xl font-black uppercase tracking-tighter hover:bg-blue-600 transition-all flex items-center shadow-2xl"
              >
                {step === 5 ? 'Submit Application' : 'Continue'}
                {step < 5 && <ChevronRight className="w-5 h-5 ml-2" />}
              </button>
            </div>
          </div>
        </form>

        <p className="mt-10 text-center text-[10px] font-black uppercase tracking-widest text-slate-400 italic">
          Your data is processed securely in compliance with UK GDPR. Account issuance is subject to manual verification.
        </p>
      </div>
    </div>
  );
};

