import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { CheckCircle2, ChevronRight, ChevronLeft, Shield, Building, User, HelpCircle, Mail } from 'lucide-react';
import { INDUSTRIES, COUNTRIES } from '../constants';
import { SignupFormData, Page } from '../types';

interface SignupProps {
  onNavigate: (page: Page) => void;
}

const Signup: React.FC<SignupProps> = ({ onNavigate }) => {
  const [step, setStep] = useState(1);
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [formData, setFormData] = useState<SignupFormData>({
    fullName: '',
    email: '',
    companyName: '',
    accountType: 'Individual',
    intendedUse: '',
    emailCount: '',
    preferredUsername: '',
    industry: 'Architecture & Design',
    country: 'United Kingdom',
    websiteUrl: '',
    monthlyUsage: '',
    adminAccess: 'No',
    teamAccounts: 'No',
    complianceReqs: '',
    storageReqs: '',
    securityExpectations: '',
    verificationDocs: 'No',
    timeline: '',
    additionalNotes: '',
    agreedToTerms: false
  });

  const updateField = (field: keyof SignupFormData, value: any) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const nextStep = () => setStep(s => Math.min(s + 1, 5));
  const prevStep = () => setStep(s => Math.max(s - 1, 1));

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (step === 5) {
      setIsSubmitted(true);
      window.scrollTo(0, 0);
    } else {
      nextStep();
    }
  };

  if (isSubmitted) {
    return (
      <div className="min-h-[90vh] flex items-center justify-center p-4 bg-[#020617]">
        <motion.div 
          initial={{ opacity: 0, scale: 0.95 }}
          animate={{ opacity: 1, scale: 1 }}
          className="max-w-2xl w-full text-center bg-slate-950 p-20 border border-white/5 shadow-4xl"
        >
          <div className="bg-amber-500 w-28 h-28 flex items-center justify-center mx-auto mb-12 shadow-2xl">
            <CheckCircle2 className="w-14 h-14 text-slate-900" />
          </div>
          <h2 className="text-5xl font-black text-white mb-8 tracking-tighter uppercase italic leading-none">Induction <br /><span className="gold-gradient">Confirmed.</span></h2>
          <p className="text-slate-400 mb-12 text-xl font-light italic leading-relaxed opacity-80">
            The tempering process has begun. Our London-based site managers will review your industrial profile within 24‚Äì48 hours. You will receive notification once structural access is granted.
          </p>
          <button 
            onClick={() => onNavigate('home')}
            className="w-full bg-amber-500 text-slate-900 py-7 text-xl font-black uppercase tracking-tighter hover:bg-white transition-all shadow-3xl shadow-amber-500/10"
          >
            Return to Workstation
          </button>
        </motion.div>
      </div>
    );
  }

  const renderStep = () => {
    switch (step) {
      case 1:
        return (
          <motion.div initial={{ opacity: 0, x: 20 }} animate={{ opacity: 1, x: 0 }} className="space-y-10">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-10">
              <div>
                <label className="block text-[10px] font-black uppercase tracking-[0.3em] text-amber-500/60 mb-4 italic">Full Legal Name</label>
                <input 
                  type="text" required
