                {step === 2 && <Mail className="w-10 h-10" />}
                {step === 3 && <Building className="w-10 h-10" />}
                {step === 4 && <Shield className="w-10 h-10" />}
                {step === 5 && <Radio className="w-10 h-10" />}
              </div>
              <div>
                <h3 className="text-3xl font-extrabold text-white tracking-tighter uppercase italic leading-none">
                  {step === 1 && 'Pulse Identity'}
                  {step === 2 && 'Signal Intent'}
                  {step === 3 && 'Nexus Profile'}
                  {step === 4 && 'Lattice Shield'}
                  {step === 5 && 'Harmonic Final'}
                </h3>
                <p className="mono text-[8px] font-bold uppercase tracking-[0.4em] text-cyan-400/40 mt-3 italic">Phase {step} of 5</p>
              </div>
            </div>

            {renderStep()}

            <div className="mt-20 pt-12 border-t border-white/5 flex items-center justify-between">
              <button
                type="button"
                onClick={prevStep}
                disabled={step === 1}
                className={`flex items-center mono font-bold uppercase tracking-widest text-[10px] px-10 py-5 transition-all ${
                  step === 1 ? 'opacity-0 cursor-default' : 'text-slate-500 hover:text-white'
                }`}
              >
                <ChevronLeft className="w-6 h-6 mr-3" />
                Rewind
              </button>
              <button
                type="submit"
                className="bg-cyan-500 text-slate-950 px-16 py-6 rounded-full text-xs font-extrabold uppercase tracking-widest hover:bg-white transition-all flex items-center shadow-3xl shadow-cyan-400/20"
              >
                {step === 5 ? 'Execute Sync' : 'Proceed'}
                {step < 5 && <ChevronRight className="w-6 h-6 ml-3" />}
              </button>
            </div>
          </div>
        </form>

        <p className="mt-12 text-center mono text-[8px] font-bold uppercase tracking-[0.5em] text-slate-700 italic">
          Data Orchestration: Global Lattice Node | Encrypted with Quantum-Resistant AES-256
        </p>
      </div>
    </div>
  );
};

